INSERT INTO `tb_atribuicao` (`codigo_atribuicao`, `descricao_atribuicao`, `complemento_atribuicao`) VALUES
('41043', 'ARTIGO 3º DA RESOLUÇÃO 313/86 DO CONFEA DE ACORDO COM O QUE ESTABELECE A RESOLUÇÃO 313/86 DO CONFEA, E EM FUNÇÃO DAS CARACTERÍSTICAS CURRICULARES DO CURSO SUPERIOR DE TECNOLOGIA CURSADO PELO PROFISSIONAL ESTE TEM O SEGUINTE CAMPO DE ATUAÇÃO DEFINIDO ESTANDO APTO PARA DESENVOLVER ATIVIDADES NAS SEGUINTES ÁREAS, DESDE QUE SEMPRE LIMITADOS AS CARACTERÍSTICAS DE FORMAÇÃO INFORMADAS PELA INSTITUIÇÃO DE ENSINO, COMO DESCRITO À SEGUIR: CONTROLE E AUTOMAÇÃO DE EQUIPAMENTOS, PROCESSOS, UNIDADES E SISTEMAS DE PRODUÇÃO. \"CONFORME ESTABELECIDO PELA RESOLUÇÃO 313/86 DO CONFEA: ART. 3º - AS ATRIBUIÇÕES DOS TECNÓLOGOS, EM SUAS DIVERSAS MODALIDADES, PARA EFEITO DO EXERCÍCIO PROFISSIONAL, E DA SUA FISCALIZAÇÃO, RESPEITADOS OS LIMITES DE SUA FORMAÇÃO, CONSISTEM EM: 1) ELABORAÇÃO DE ORÇAMENTO; 2) PADRONIZAÇÃO, MENSURAÇÃO E CONTROLE DE QUALIDADE; 3) CONDUÇÃO DE TRABALHO TÉCNICO; 4) CONDUÇÃO DE EQUIPE DE INSTALAÇÃO, MONTAGEM, OPERAÇÃO, REPARO OU MANUTENÇÃO; 5) EXECUÇÃO DE INSTALAÇÃO, MONTAGEM E REPARO; 6) OPERAÇÃO E MANUTENÇÃO DE EQUIPAMENTO E INSTALAÇÃO; 7) EXECUÇÃO DE DESENHO TÉCNICO. PARÁGRAFO ÚNICO - COMPETE, AINDA, AOS TECNÓLOGOS EM SUAS DIVERSAS MODALIDADES, SOB A SUPERVISÃO E DIREÇÃO DE ENGENHEIROS, ARQUITETOS OU ENGENHEIROS AGRÔNOMOS: 1) EXECUÇÃO DE OBRA E SERVIÇO TÉCNICO; 2) FISCALIZAÇÃO DE OBRA E SERVIÇO TÉCNICO; 3) PRODUÇÃO TÉCNICA ESPECIALIZADA. ART. 4º - QUANDO ENQUADRADAS, EXCLUSIVAMENTE, NO DESEMPENHO DAS ATIVIDADES REFERIDAS NO ART. 3º E SEU PARÁGRAFO ÚNICO, PODERÃO OS TECNÓLOGOS EXERCER AS SEGUINTES ATIVIDADES: 1) VISTORIA, PERICIA, AVALIAÇÃO, ARBITRAMENTO, LAUDO E PARECER TÉCNICO; 2) DESEMPENHO DE CARGO E FUNÇÃO TÉCNICA; 3) ENSINO, PESQUISA, ANÁLISE, EXPERIMENTAÇÃO, ENSAIO E DIVULGAÇÃO TÉCNICA, EXTENSÃO. PARÁGRAFO ÚNICO - O TECNÓLOGO PODERÁ RESPONSABILIZAR-SE, TECNICAMENTE, POR PESSOA JURÍDICA, DESDE QUE O OBJETIVO SOCIAL DESTA SEJA COMPATÍVEL COM SUAS ATRIBUIÇÕES. ART. 5º - NENHUM PROFISSIONAL PODERÁ DESEMPENHAR ATIVIDADES ALÉM DAQUELAS QUE LHE COMPETEM, PELAS CARACTERÍSTICAS DO SEU CURRÍCULO ESCOLAR, CONSIDERADAS EM CADA CASO APENAS AS DISCIPLINAS QUE CONTRIBUEM PARA A GRADUAÇÃO PROFISSIONAL, SALVO OUTRAS QUE LHE SEJAM ACRESCIDAS EM CURSO DE PÓS-GRADUAÇÃO, NA MESMA MODALIDADE. \"CONFORME INFORMADO PELA INSTITUIÇÃO DE ENSINO EM SEU PROJETO DE CURSO TEMOS AS SEGUINTES CARACTERÍSTICAS DE FORMAÇÃO, SENDO QUE TAL PERFIL NÃO IMPLICA EM EXTENSÃO DAS ATRIBUIÇÕES DEFINIDAS PELA RESOLUÇÃO 313/86 DO CONFEA. \"OBJETIVO GERAL: OFERTAR O CURSO SUPERIOR DE TECNOLOGIA EM AUTOMAÇÃO INDUSTRIAL VISANDO ATENDER AS NECESSIDADES DAS EMPRESAS NESTA ÁREA E FORMAR PROFISSIONAIS PARA ATUAR NO MERCADO DE TRABALHO. OBJETIVOS ESPECÍFICOS: A) POSSIBILITAR AO ALUNO A PARTICIPAÇÃO DE FORMA RESPONSÁVEL, ATIVA, CRITICA E CRIATIVA NA VIDA EM SOCIEDADE; B) FORMAR PROFISSIONAIS PARA A ÁREA DE AUTOMAÇÃO INDUSTRIAL COM EMBASAMENTO TÉCNICO E PRATICO; C) CAPACITAR O ALUNO A IMPLEMENTAR SISTEMAS NA ÁREA DE AUTOMAÇÃO INDUSTRIAL, BEM COMO CIRCUITOS NECESSÁRIOS PARA O INTERFACEAMENTO ENTRE OS  SUBSISTEMAS; D) FORMAR PROFISSIONAIS COM CAPACIDADE DE PLANEJAR, EXECUTAR, SUPERVISIONAR E PARTICIPAR DA INOVAÇÃO DE SISTEMAS NA ÁREA DE AUTOMAÇÃO INDUSTRIAL; E) CAPACITAR O ALUNO A APLICAR FERRAMENTAS DE GESTÃO TECNOLÓGICA DE PROCESSOS INDUSTRIAIS. POSTOS DE TRABALHO: DENTRE AS DIVERSAS FUNÇÕES QUE O TECNÓLOGO EM AUTOMAÇÃO INDUSTRIAL FORMADO PELA UNIDADE DE CURITIBA DO CEFET-PR PODE ASSUMIR, DESTACAM-SE: 7 EXECUTOR DE INSTALAÇÕES ELÉTRICAS DE SISTEMAS AUTOMATIZADOS; 7 INSTALADOR E MANTENEDOR DE EQUIPAMENTOS ELETROELETRÔNICOS; 7 ASSESSOR NA GESTÃO DE CONSERVAÇÃO DE ENERGIA; 7 COORDENADOR DE EQUIPES DE MANUTENÇÃO DE SISTEMAS AUTOMATIZADOS; 7 INSTALADOR E MANTENEDOR DE SISTEMAS: ELETROMAGNÉTICOS, ELETROPNEUMÁTICOS E ELETRO-HIDRÁULICOS; 7 EXECUTOR DE INSTALAÇÕES DE SISTEMAS AUTOMATIZADOS UTILIZANDO CLP; 7  COORDENADOR DE EQUIPE DE IMPLEMENTAÇÃO DE PROJETOS AUTOMATIZADOS; - OPERADOR DE SISTEMAS DE PLANTAS INDUSTRIAIS AUTOMATIZADAS;7 INSTALADOR E OPERADOR DE SISTEMAS DE REDES INDUSTRIAIS; 7 INTEGRADOR EM SISTEMAS DE MANUFATURA; 7 GERENTE DE SISTEMAS AUTOMATIZADOS; INSTRUMENTISTA EM SISTEMAS AUTOMATIZADOS. O PERFIL DO TECNÓLOGO EM AUTOMAÇÃO INDUSTRIAL CONTEMPLARA AS LINHAS DE FORMAÇÃO BÁSICA, PESSOAL E TÉCNICA. A IMPLEMENTAÇÃO DO CURSO VISA A INTERDISCIPLINARIDADE, POR MEIO DE UM CURRÍCULO ESTRUTURADO NAS BASES DO CONHECIMENTO CIENTÍFICO, TECNOLÓGICO E DE GESTÃO. O TECNÓLOGO EM AUTOMAÇÃO INDUSTRIAL É UM PROFISSIONAL PRESTATIVO, COM CAPACIDADE DE RELACIONAMENTO INTERPESSOAL E FACILIDADE DE COMUNICAÇÃO. O RESPEITO AO MEIO AMBIENTE, À ADEQUAÇÃO E ÀS MUDANÇAS TECNOLÓGICAS SÃO CARACTERÍSTICAS MARCANTES DESTE PROFISSIONAL. AS COMPETÊNCIAS ADQUIRIDAS AO LONGO DO CURSO SÃO: 7 INTERPRETAR PLANTAS E CORTES DE ESTRUTURAS E EQUIPAMENTOS; 7 INTERPRETAR PLANTAS DE PROJETOS ELÉTRICOS DE SISTEMAS AUTOMATIZADOS; 7 INSTALAR, OPERAR E PARAMETRIZAR CONVERSORES, INVERSORES DE FREQUÊNCIA E SOFTSTARTERS 7 APLICAR NORMAS TÉCNICAS RELACIONADAS A INFRAESTRUTURA DE INSTALAÇÕES ELÉTRICAS PARA SISTEMAS DE AUTOMAÇÃO; 7 OPERAR E MANTER SISTEMAS CAD, CAE, CAM E CNC; 7 EXECUTAR AS BASES LEGAIS REFERENTES À SEGURANÇA NO TRABALHO EM AMBIENTES AUTOMATIZADOS; 7 EXECUTAR INSTALAÇÕES DE INFRAESTRUTURA ELÉTRICA PARA SISTEMAS DE AUTOMAÇÃO; 7 EXECUTAR MANUTENÇÃO DE SISTEMAS AUTOMATIZADOS; 7 IMPLEMENTAR SISTEMAS AUTOMATIZADOS UTILIZANDO CONTROLADOR LÓGICO PROGRAMÁVEL BEM COMO MICROCONTROLADORES; 7 IMPLEMENTAR REDES INDUSTRIAIS; 7 IMPLEMENTAR SISTEMAS NA ÁREA DE AUTOMAÇÃO INDUSTRIAL, BEM COMO CIRCUITOS NECESSÁRIOS PARA O INTERFACEAMENTO ENTRE OS SUBSISTEMAS; 7 INTEGRAR DISPOSITIVOS DE ACIONAMENTOS INDUSTRIAIS. 7 INTEGRAR FONTES\r\nDE ENERGIA: ALTERNATIVAS, DE CONCESSIONÁRIAS OU DE EMERGÊNCIA, CONSIDERANDO OS ASPECTOS DE EFICIÊNCIA ENERGÉTICA E DA AUTOMAÇÃO; 7 INTEGRAR SISTEMAS AUTOMATIZADOS UTILIZANDO HARDWARE E SOFTWARE; 7 ASSESSORAR A ELABORAÇÃO DE PLANOS DE CONSERVAÇÃO DE ENERGIA EM\r\nINSTALAÇÕES ELÉTRICAS, EXECUTANDO ANALISE DE SISTEMAS TARIFÁRIOS COMERCIAIS E INDUSTRIAIS; 7 ANALISAR CIRCUITOS DE COMANDOS ELETROMAGNÉTICOS, ELETROPNEUMÁTICOS E ELETRO-HIDRÁULICOS EM SISTEMAS AUTOMATIZADOS; 7 PLANEJAR, EXECUTAR, SUPERVISIONAR E INOVAR SISTEMAS NA ÁREA DE AUTOMAÇÃO INDUSTRIAL; 7 PLANEJAR O ACIONAMENTO DE MAQUINAS UTILIZANDO OS PRINCÍPIOS DE CONTROLE ELETRÔNICO; 7 OPERAR PLANTAS CONTROLADAS POR SISTEMAS SUPERVISÓRIOS; 7 CONDUZIR EQUIPES E PROJETOS LABORAIS EM SISTEMAS AUTOMATIZADOS. 7 GERENCIAR IMPLEMENTAÇÕES E MANUTENÇÕES DE SISTEMAS AUTOMATIZADOS; 7 GERENCIAR EMPRESA NA ÁREA DE AUTOMAÇÃO INDUSTRIAL; 7 GERENCIAR PROCESSOS INDUSTRIAIS AUTOMATIZADOS APLICANDO FERRAMENTAS DE GESTÃO TECNOLÓGICA; RESTRIÇÕES: DE ACORDO COM O QUE ESTABELECE A LEI 5194/66 E A RESOLUÇÃO 313 DO CONFEA E EM FUNÇÃO DAS CARACTERÍSTICAS CURRICULARES DO CURSO FREQUENTADO O PROFISSIONAL POSSUI RESTRIÇÃO PARA SISTEMAS DE AUTOMAÇÃO NÃO INDUSTRIAIS.', 'ARTIGO 3º DA RESOLUÇÃO 313/86 DO CONFEA DE ACORDO COM O QUE ESTABELECE A RESOLUÇÃO 313/86 DO CONFEA, E EM FUNÇÃO DAS CARACTERÍSTICAS CURRICULARES DO CURSO SUPERIOR DE TECNOLOGIA CURSADO PELO PROFISSIONAL ESTE TEM O SEGUINTE CAMPO DE ATUAÇÃO DEFINIDO ESTANDO APTO PARA DESENVOLVER ATIVIDADES NAS SEGUINTES ÁREAS, DESDE QUE SEMPRE LIMITADOS AS CARACTERÍSTICAS DE FORMAÇÃO INFORMADAS PELA INSTITUIÇÃO DE ENSINO, COMO DESCRITO À SEGUIR: CONTROLE E AUTOMAÇÃO DE EQUIPAMENTOS, PROCESSOS, UNIDADES E SISTEMAS DE PRODUÇÃO. \"CONFORME ESTABELECIDO PELA RESOLUÇÃO 313/86 DO CONFEA: ART. 3º - AS ATRIBUIÇÕES DOS TECNÓLOGOS, EM SUAS DIVERSAS MODALIDADES, PARA EFEITO DO EXERCÍCIO PROFISSIONAL, E DA SUA FISCALIZAÇÃO, RESPEITADOS OS LIMITES DE SUA FORMAÇÃO, CONSISTEM EM: 1) ELABORAÇÃO DE ORÇAMENTO; 2) PADRONIZAÇÃO, MENSURAÇÃO E CONTROLE DE QUALIDADE; 3) CONDUÇÃO DE TRABALHO TÉCNICO; 4) CONDUÇÃO DE EQUIPE DE INSTALAÇÃO, MONTAGEM, OPERAÇÃO, REPARO OU MANUTENÇÃO; 5) EXECUÇÃO DE INSTALAÇÃO, MONTAGEM E REPARO; 6) OPERAÇÃO E MANUTENÇÃO DE EQUIPAMENTO E INSTALAÇÃO; 7) EXECUÇÃO DE DESENHO TÉCNICO. PARÁGRAFO ÚNICO - COMPETE, AINDA, AOS TECNÓLOGOS EM SUAS DIVE